print.mcarray <- function (x) {  ndim <- length(dim(x))    xx <- x  class(xx) <- 'array'  xx <- apply(xx, 1, mean)  print(xx)    invisible(x)}print.gsmcmc <- function (x) {  cat("\nparameters\n")      print_list <- function (x) {    for (par in names(x)) {      print(par)      print(x[[par]])    }  }    print_list(x$mcmc)      cat("\nchains\n")  print(nchain(x))  cat("\nsimulations\n")  print(nsim(x))    invisible(x)}print.gsmodel <- function (x) {  if(!is.null(x$name)) {    cat("\nname\n")    print(print(x$name))  }  cat("\nmodel\n")  cat(x$model)  cat("\n")    if(!is.null(x$derived)) {    cat("\nderived\n")    cat(x$derived)    cat("\n")  }  if(!is.null(x$monitor)) {    cat("\nmonitor\n")    print(x$monitor)  }    if(!is.null(x$gen_inits)) {    cat("\ngen_inits\n")    print(x$gen_inits)     }  if(!is.null(x$select)) {    cat("\nselect\n")    print(x$select)     }    if(!is.null(x$standardise)) {    cat("\nstandardise\n")    print(x$standardise)  }  if(!is.null(x$random)) {    cat("\nrandom\n")    print(x$random)  }  if(!is.null(x$translate)) {    cat("\ntranslate\n")    print(x$translate)  }  invisible(x)}print.gsanalysis <- function (x) {  print(x$model)   cat("\ndata\n")  print(head(x$data))   cat("\ninits\n")  print(x$inits)  cat("\niterations\n")  print(x$iterations)  cat("\ntime\n")  print(x$time)  print(x$mcmc)    invisible(x)}print.summary_jagr_analysis <- function (x) {  for (name in names(x)) {    cat(paste0("\n",name,":\n"))    print(x[[name]])  }}print.summary_janalysis <- function (x) {    for (name in names(x)) {    cat(paste0("\n",name,":\n"))    print(x[[name]])  }}